  protected function build_table_personal($personal) {
    $html_content = '<table style="width: 100%; border: 1px solid black; border-collapse: collapse; text-align: left; font-size:14px;">';
    $html_content .= '<tr style="background-color:yellow;"><th colspan="4" style="border: 1px solid black; text-align: center;">PERSONAL DEL ESTABLECIMIENTO</th></tr>';
    $html_content .= '<tr style="background-color:yellow; text-align: center;"><th style="border: 1px solid black;">GÉNERO</th><th style="border: 1px solid black;">DESCRIPCIÓN</th><th style="border: 1px solid black;">INFORMACIÓN USUARIO</th><th style="border: 1px solid black;">INFORMACIÓN TÉCNICO ZONAL</th></tr>';
    $total = 0;
    foreach($personal as $grupo) {
      if (trim($grupo['worker_group_name']) == "TOTAL de empleados del establecimiento") {
        $html_content .= '<tr style="background-color:silver;"><th style="border: 1px solid black;">' . $grupo['gender_name'] . '</th><th style="border: 1px solid black;">' . $grupo['worker_group_name'] . '</th><th style="border: 1px solid black; text-align:center;">' . $grupo['count'] . '</th><td style="border: 1px solid black;"></td></tr>';
        $total += $grupo['count'];
      } else {
        $html_content .= '<tr><th style="border: 1px solid black;">' . $grupo['gender_name'] . '</th><th style="border: 1px solid black;">' . $grupo['worker_group_name'] . '</th><td style="border: 1px solid black; text-align:center;">' . $grupo['count'] . '</td><td style="border: 1px solid black;"></td></tr>';
      }
    }
    $html_content .= '<tr style="background-color:silver;"><th style="border: 1px solid black;"></th><th style="border: 1px solid black;">TOTAL DE TRABAJADORES DEL ESTABLECIMIENTO</th><th style="border: 1px solid black; text-align:center;">' . $total . '</th><td style="border: 1px solid black;"></td></tr>';
    $html_content .= '</table>';
    return $html_content;
  }

  function pdf_tarifario_rack(Request $data) {
    $request = $data->json()->all();
    $html_content = '<pagina><div style="width:100%; height:350px;"></div><div style="width:100%; margin-left: 150px; margin-right:100px;">';
    $html_content .= '<p style="text-align:right;"><strong>##canton##, ##fecha##<br/><br/></strong>';
    $html_content .= '</p><p style="text-align: justify">Conforme a lo determinado en la disposición general tercera del Reglamento General de Alojamiento "Los establecimientos de alojamiento deberán informar a la Autoridad Nacional de Turismo hasta el primero de diciembre de cada año sobre las tarifas rack o mostrador del año siguiente. Estas considerarán el valor por huésped por noche por tipo de habitación y por temporada alta y baja, incluido impuesto; de no hacerlo la Autoridad Nacional de Turismo sancionará conforme a la normativa vigente", para el cumplimiento se procede a detallar a continuación, la siguiente información:</p>';
    $html_content .= '<table style="width: 100%; border: 1px solid black; border-collapse: collapse; text-align: left; font-size:14px;">';
    $html_content .= '<tr><th colspan="4" style="text-align:center; background-color:yellow;">DATOS GENERALES</th></tr>';
    $html_content .= '<tr><th style="border: 1px solid black;">RAZÓN SOCIAL:</th><th style="border: 1px solid black;" colspan="3">##razon_social##</th></tr>';
    $html_content .= '<tr><th style="border: 1px solid black;">NOMBRE COMERCIAL:</th><th style="border: 1px solid black;">##nombre_comercial##</th><th style="border: 1px solid black;">REGISTRO:</th><th style="border: 1px solid black;">##registro##</th></tr>';
    $html_content .= '<tr><th style="border: 1px solid black;">RUC:</th><th style="border: 1px solid black;">##ruc##</th><th style="border: 1px solid black;">REPRESENTANTE LEGAL:</th><th style="border: 1px solid black;">##representante_legal##</th></tr>';
    $html_content .= '<tr><th style="border: 1px solid black;">PROVINCIA:</th><th style="border: 1px solid black;">##provincia##</th><th style="border: 1px solid black;">CANTÓN:</th><th style="border: 1px solid black;">##canton##</th></tr>';
    $html_content .= '<tr><th style="border: 1px solid black;">DIRECCIÓN:</th><th style="border: 1px solid black;" colspan="3">##direccion##</th></tr>';
    $html_content .= '<tr><th style="border: 1px solid black;">CLASIFICACIÓN:</th><th style="border: 1px solid black;">##clasificacion##</th><th style="border: 1px solid black;">CATEGORÍA:</th><th style="border: 1px solid black;">##categoria##</th></tr>';
    $html_content .= '</table><br/>';
    $html_content .= $this->build_table_tarifario_reporte($request['tariffs']);
    $html_content .= '<p style="text-align:left; font-size:14px;"><strong><i>*Precios incluyen IVA</i></strong></p><br /><br /><br /><br />';
    $html_content .= '</div></pagina>';
    try {
      $qr = $request['qr'];
    } catch (Exception $e) {
      $qr = false;
    }
    try {
      $qr_content = $request['qr_content'];
    } catch (Exception $e) {
      $qr_content = '';
    }
    try {
      $params = $request['params'];
    } catch (Exception $e) {
      $params = [];
    }
    if (!$params) {
      $params = [];
    }
    $title = 'TARIFARIO RACK O MOSTRADOR';
    
    $pdf_content = $this->build_content($html_content, $params);
    $html = $this->mintur_style($pdf_content, $title, $qr, $qr_content);
    $orientation = 'portrait';
    $pdf = App::make('dompdf.wrapper');
    $pdf->setPaper('A4', $orientation);
    $pdf->setOptions(['dpi' => 150, 'defaultFont' => 'courier']);
    $pdf->loadHTML($html);
    $bytes = $pdf->output();
    $toReturn = base64_encode($bytes);
    return response()->json($toReturn, 200);
  }

  protected function buildPagesChecklist($requisites) {
    $toReturn = '';
    $paginas = 0;
    $requisitosImpresos = 0;
    $requisitesPerPage = 30;
    $pageContent = '<table style="width: 100%; border: 1px solid black; border-collapse: collapse; text-align: left;">';
    $pageContent .= '<tr style="background-color:yellow; font-size:14px;"><th style="border: 1px solid black;">Código</th><th style="border: 1px solid black;">Descripción</th><th style="border: 1px solid black;">Usuario</th><th style="border: 1px solid black;">Verificación</th></tr>';
    foreach ($requisites as $requisite) {
      $mandatory = ' (OBLIGATORIO) ';
      if ($requisite['mandatory'] == true) {
        $mandatory = ' (OBLIGATORIO) ';
      } else {
        $mandatory = '';
      }
      if ($requisite['HTMLtype'] == 'GRUPO 0') {
        $pageContent .= '<tr style="background-color:yellow;"><td></td><th style="border: 1px solid black; font-size: 16px;" colspan="3">'. $requisite['requisite_name'].'</th></tr>';
      }
      if ($requisite['HTMLtype'] == 'GRUPO 1') {
        $pageContent .= '<tr style="background-color:LightGray;"><td></td><th style="border: 1px solid black; font-size: 16px;" colspan="3">'. $requisite['requisite_name'].'</th></tr>';
      }
      if ($requisite['HTMLtype'] == 'GRUPO 2') {
        $pageContent .= '<tr style="background-color:LightGray;"><td></td><th style="border: 1px solid black; font-size: 16px;" colspan="3">'. $requisite['requisite_name'].'</th></tr>';
      }
      if ($requisite['HTMLtype'] == 'GRUPO 3') {
        $pageContent .= '<tr style="background-color:LightGray;"><td></td><th style="border: 1px solid black; font-size: 16px;" colspan="3">'. $requisite['requisite_name'].'</th></tr>';
      }
      if ($requisite['HTMLtype'] == 'GRUPO 4') {
        $pageContent .= '<tr style="background-color:LightGray;"><td></td><th style="border: 1px solid black; font-size: 16px;" colspan="3">'. $requisite['requisite_name'].'</th></tr>';
      }
      if ($requisite['HTMLtype'] == 'GRUPO 5') {
        $pageContent .= '<tr style="background-color:LightGray;"><td></td><th style="border: 1px solid black; font-size: 16px;" colspan="3">'. $requisite['requisite_name'].'</th></tr>';
      }
      if ($requisite['HTMLtype'] == 'TRUE / FALSE') {
        $cumple = "CUMPLE";
        if ($requisite['value'] == 'true') {
          $cumple = "CUMPLE";
        } else {
          $cumple = "NO CUMPLE";
        }
        $pageContent .= '<tr><th style="border: 1px solid black; font-size: 14px;">' . $requisite['requisite_id'] . '</th><td style="border: 1px solid black; font-size: 14px;">'. $requisite['requisite_name']. $mandatory .'</td><td style="border: 1px solid black; font-size: 14px; text-align:center;">' . $cumple . '</td><td style="border: 1px solid black;">&nbsp;&nbsp;&nbsp;&nbsp;</td></tr>';
      }
      if ($requisite['HTMLtype'] == 'YES / NO') {
        $pageContent .= '<tr><th style="border: 1px solid black; font-size: 14px;">' . $requisite['requisite_id'] . '</th><td style="border: 1px solid black; font-size: 14px;">'. $requisite['requisite_name']. $mandatory .'</td><td style="border: 1px solid black; font-size: 14px; text-align:center;">' . $requisite['value'] . '</td><td style="border: 1px solid black;">&nbsp;&nbsp;&nbsp;&nbsp;</td></tr>';
      }
      if ($requisite['HTMLtype'] == 'NUMBER') {
        $pageContent .= '<tr><th style="border: 1px solid black; font-size: 14px;">' . $requisite['requisite_id'] . '</th><td style="border: 1px solid black; font-size: 14px;">'. $requisite['requisite_name']. $mandatory .'</td><td style="border: 1px solid black; font-size: 14px; text-align:center;">' . $requisite['value'] . '</td><td style="border: 1px solid black;">&nbsp;&nbsp;&nbsp;&nbsp;</td></tr>';
      }
      $requisitosImpresos++;
      if($requisitosImpresos % $requisitesPerPage == 0) {
        $paginas++;
        $pageContent .= '</table>';
        $toReturn .= '<pagina><div style="width:100%; height:350px;"></div><div style="width:100%; margin-left: 150px; margin-right:100px;">';
